create table tmp_parameter
(
    id                  uuid,
    action_id           uuid,
    name                varchar(50),
    type                varchar(50),
    default_value       text,
    title               text,
    editable            boolean,
    "order"             integer,
    dictionary_id       uuid,
    name_column         varchar,
    value_column        varchar,
    editable_dictionary boolean,
    dict_null_value     boolean
);

alter table tmp_parameter
    owner to postgres;

